      name: "Tuna",
      salesPrice: 7.49,
      units: 10,
      onSale: true,
      category: "Fish",
    },
    {
      number: "45454",
      name: "Carrot",
      salesPrice: 1.99,
      units: 35,
      onSale: false,
      category: "Vegetable",
    },
    {
      number: "67676",
      name: "Lettuce",
      salesPrice: 2.49,
      units: 45,
      onSale: true,
      category: "Vegetable",
    },
  ];

  const [rowData, setRowData] = useState(initialRowData);
  const [editingRowIndex, setEditingRowIndex] = useState(null);
  const [formData, setFormData] = useState({
    number: "",
    name: "",
    salesPrice: "",
    units: "",
    onSale: false,
    category: "",
    sortBy: "", // New state for sorting
  });
  const [showForm, setShowForm] = useState(false);
  const [selectedCategories, setSelectedCategories] = useState([]);

  const handleEditClick = (index) => {
    setEditingRowIndex(index);
  };

  const handleSaveClick = () => {
    setEditingRowIndex(null);
    // Here you can handle saving the updated values, e.g., making an API call
  };

  const handleAddButtonClick = () => {
    setShowForm((prevShowForm) => !prevShowForm);
  };

  const handleFormInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();

    // Sort the table data based on the selected value in the dropdown list
    let sortedData = [...initialRowData];
    if (formData.sortBy === "name") {
      sortedData.sort((a, b) => a.name.localeCompare(b.name));
    } else if (formData.sortBy === "units") {
      sortedData.sort((a, b) => a.units - b.units);
    }

    // Filter the sorted data based on selected categories
    const filteredData = sortedData.filter((product) =>
      selectedCategories.includes(product.category)
    );

    // If "All" option is selected, show all products
    if (selectedCategories.includes("All")) {
      setSelectedCategories(initialRowData.map((product) => product.category));
    } else {
      setRowData(filteredData);
    }
  };
  const handleFormSubmitAdd = (e) => {
    e.preventDefault();
    const newRowData = [...rowData, formData];
    setRowData(newRowData);
    setShowForm(false);
    setFormData({
      id: "",
      name: "",
      producer: "",
      category: "",
      characteristics: "",
    });
  };

  const handleCheckboxChange = (e) => {
    const { value, checked } = e.target;
    if (value === "All") {
      if (checked) {
        setSelectedCategories(
          initialRowData.map((product) => product.category)
        );
      } else {
        setSelectedCategories([]);
      }
    } else {
      if (checked) {
        setSelectedCategories((prevState) => [...prevState, value]);
      } else {
        setSelectedCategories((prevState) =>
          prevState.filter((category) => category !== value)
        );
      }
    }
  };

  const handleDeleteClick = (index) => {
    const updatedData = [...rowData];
    updatedData.splice(index, 1);
    setRowData(updatedData);
  };
  const handlePrintButtonClick = () => {
    window.print();
  };

  return (
    <div className="product-container container">
      <form onSubmit={handleFormSubmit}>
        <label>
          <input
            type="checkbox"
            name="category"
            value="All"
            onChange={handleCheckboxChange}
          />
          All
        </label>
        <label>
          <input
            type="checkbox"
            name="category"
            value="Fruit"
            onChange={handleCheckboxChange}
          />
          Fruit
        </label>
        <label>
          <input
            type="checkbox"
            name="category"
            value="Bakery"
            onChange={handleCheckboxChange}
          />
          Bakery
        </label>
        <label>
          <input
            type="checkbox"
            name="category"
            value="Dairy"
            onChange={handleCheckboxChange}
          />
          Dairy
        </label>
        <label>
          <input
            type="checkbox"
            name="category"
            value="Fish"
            onChange={handleCheckboxChange}
          />
          Fish
        </label>
        <label>
          <input
            type="checkbox"
            name="category"
            value="Vegetable"
            onChange={handleCheckboxChange}
          />
          Vegetable
        </label>
        <select
          name="sortBy"
          value={formData.sortBy}
          onChange={handleFormInputChange}
        >
          <option value="">Sort By</option>
          <option value="name">Name</option>
          <option value="units">Number of Units</option>
        </select>
        <button type="submit">Filter</button>
      </form>
      <button className="btn-print" onClick={handlePrintButtonClick}>
        Print
      </button>
      <button className="btn-add" onClick={handleAddButtonClick}>
        Add
      </button>
      {showForm && (
        <form onSubmit={handleFormSubmitAdd}>
          <input
            type="text"
            name="number"
            value={formData.number}
            onChange={handleFormInputChange}
            placeholder="Number"
          />
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleFormInputChange}
            placeholder="Name"
          />
          <input
            type="number"
            name="salesPrice"
            value={formData.salesPrice}
            onChange={handleFormInputChange}
            placeholder="Sales Price"
          />
          <input
            type="number"
            name="units"
            value={formData.units}
            onChange={handleFormInputChange}
            placeholder="Units"
          />
          <label>
            <input
              type="checkbox"
              name="onSale"
              checked={formData.onSale}
              onChange={(e) =>
                setFormData({ ...formData, onSale: e.target.checked })
              }
            />
            On Sale
          </label>
          <select
            name="category"
            value={formData.category}
            onChange={handleFormInputChange}
          >
            <option value="">Select Category</option>
            <option value="Fruit">Fruit</option>
            <option value="Bakery">Bakery</option>
            <option value="Dairy">Dairy</option>
            <option value="Fish">Fish</option>
            <option value="Vegetable">Vegetable</option>
          </select>
          <button type="submit">Save</button>
        </form>
      )}
      <table>
        <thead>
          <tr>
            <th>Number</th>
            <th>Name</th>
            <th>Sales Price</th>
            <th>Units</th>
            <th>On Sale</th>
            <th>Category</th>
            <th></th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {rowData.map((row, index) => (
            <tr key={index}>
              <td>
                {editingRowIndex === index ? (
                  <input
                    type="text"
                    value={row.number}
                    onChange={(e) => {
                      const updatedData = [...rowData];
                      updatedData[index].number = e.target.value;
                      setRowData(updatedData);
                    }}
                  />
                ) : (
                  row.number
                )}
              </td>
              <td>
                {editingRowIndex === index ? (
                  <input
                    type="text"
                    value={row.name}
                    onChange={(e) => {
                      const updatedData = [...rowData];
                      updatedData[index].name = e.target.value;
                      setRowData(updatedData);
                    }}
                  />
                ) : (
                  row.name
                )}
              </td>
              <td>
                {editingRowIndex === index ? (
                  <input
                    type="number"
                    value={row.salesPrice}
                    onChange={(e) => {
                      const updatedData = [...rowData];
                      updatedData[index].salesPrice = e.target.value;
                      setRowData(updatedData);
                    }}
                  />
                ) : (
                  row.salesPrice
                )}
              </td>
              <td>
                {editingRowIndex === index ? (
                  <input
                    type="number"
                    value={row.units}
                    onChange={(e) => {
                      const updatedData = [...rowData];
                      updatedData[index].units = e.target.value;
                      setRowData(updatedData);
                    }}
                  />
                ) : (
                  row.units
                )}
              </td>
              <td>
                {editingRowIndex === index ? (
                  <input
                    type="checkbox"
                    checked={row.onSale}
                    onChange={(e) => {
                      const updatedData = [...rowData];
                      updatedData[index].onSale = e.target.checked;
                      setRowData(updatedData);
                    }}
                  />
                ) : row.onSale ? (
                  "Yes"
                ) : (
                  "No"
                )}
              </td>
              <td>
                {editingRowIndex === index ? (
                  <input
                    type="text"
                    value={row.category}
                    onChange={(e) => {
                      const updatedData = [...rowData];
                      updatedData[index].category = e.target.value;
                      setRowData(updatedData);
                    }}
                  />
                ) : (
                  row.category
                )}
              </td>
              <td>
                {editingRowIndex === index ? (
                  <button className="btn-save" onClick={handleSaveClick}>
                    Save
                  </button>
                ) : (
                  <button
                    className="btn-edit"
                    onClick={() => handleEditClick(index)}
                  >
                    Edit
                  </button>
                )}
              </td>
              <td>
                <button
                  className="btn-delete"
                  onClick={() => handleDeleteClick(index)}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

